insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1002,12,'WebGUI','Nr du sletter en database Link, vil alle SQL Rapporter som benytter denne linken, slutte  fungere. En liste over de berrte rapportene blir vist i p bekreftelses siden.\r\n\r\nSom ved alle slette kommandoer, vil du bli spurt om du er helt sikker p at du nsker  fortsette med slettingen. Om du svarer Ja, s vil slettingen fortsette og det er ingen angre mulighet. Om du svarer Nei, vil du returnere til forrige skjermbilde.', 1066004892);
