insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1000,12,'WebGUI','Database Linker setter en WebGui administrator i stand til  legge til ofte benyttede databaser for bruk i SQL Rapporter. Detter frigjr SQL Rapport forfatteren fra  ha kjennskap til eller legge inn DSN, bruker, eller passord.\r\n\r\nVr klar over at alle database linker du legger inn vil vre tilgjengelig for alle innholds redaktrer. Selv om de ikke vil se database tilkoplingsinfo, s vil de vre i stand til  utfre enhver select, show eller describe commando p databasen.', 1066002807);
