insert into international (internationalId,languageId,namespace,message,lastUpdated) values (73,2,'FileManager','<b>Dateititel</b><BR>Der Text, der f&uuml;r diese Datei angezeigt wird. Falls Sie \r\nhier nichts angeben, wird der Dateiname angezeigt. \r\n<P><b>Dateiname</b><BR>W&auml;hlen Sie hier eine Datei von Ihrem lokalen Laufwerk, \r\ndie Sie uploaden m&ouml;chten. \r\n<P><b>Alternative #1</b><BR>Eine alternative Version dieser Datei. Wenn z. B. \r\ndie Datei im  JPEG-Format ist, k&ouml;nnten Sie bei der alternativen Version z. B. \r\nTIFF oder BMP ausw&auml;hlen. \r\n<P><b>Alternative #2</b><BR>Analog zur Alternative #1. \r\n<P><b>Kurze Beschreibung</b><BR>Eine kurze Beschreibung dieser Datei. Beachten \r\nSie, dass Sie Schl&uuml;sselw&ouml;rter benutzen, damit die Benutzer besser nach dieser \r\nDatei suchen k&ouml;nnen. \r\n<P><b>Gruppe, die Download benutzen kann</b><BR>W&auml;hlen Sie die Gruppe aus, die \r\ndiese Datei downloaden kann. \r\n<P><b>Als n&auml;chstes?</b><BR>M&ouml;chten Sie sofort&nbsp;einen neuen Download hinzuf&uuml;gen? \r\nOder m&ouml;chten Sie zuerst zur&uuml;ck zur Seite gehen?. </P>', 1064946894);
