insert into international (internationalId,languageId,namespace,message,lastUpdated) values (4,12,'Survey','<P>Undersøkelser setter deg i stand til å samle informasjon fra dine brukere. \r\nMed WebGUI sine undersøkelser kan du også teste kunnskapsnivå.</P>\r\n<P><STRONG>Spørsmåls rekkefølge</STRONG><BR>Rekkefølgen spørsmål vil bli stilt. \r\nSekvensielt viser spørsmålene i den rekkefølgen de ble laget. Vilkårlig viser \r\nspørsmålene i tilfeldig rekkefølge. Respons styrt viser spørsmålen sortert etter \r\nresponsen fra brukerene.</P>\r\n<P><STRONG>Modus</STRONG><BR>Som standard er Undersøkelse i Undersøkelse modus. \r\nDette gjør at du kan stille spørsmål til brukerene dine. Om du bytter til Test \r\nmodus kan du lage en selvrettende test av dine brukeres kunnskapsnivå.</P>\r\n<P><STRONG>Anonym respons?<BR></STRONG>Velg om undersøkelsen ta vare påog vise \r\ninformasjon som kan identifisere en bruker og hans respons. Om standardverdien \r\n"Nei" beholdes, vil undersøkelsen ta vare på brukerens IP adresse sammen med \r\nderes WebGUI bruker ID og brukernavn om de er logget inn. Denne infoen vil være \r\ntilgjengelig i rapportene. Om den settes til "Ja" vil disse feltene kun \r\ninneholde random data som ikke kan spores til noen spesiell bruker.</P>\r\n<P><STRONG>Hvem kan ta undersøkelsen?<BR></STRONG>Hvilke brukere kan delta i \r\nundersøkelsen?</P>\r\n<P><STRONG>Hvem kan se rapporter?</STRONG><BR>Hvem kan se resultatene av en \r\nundersøkelse?</P>\r\n<P><STRONG>Hva nå?</STRONG><BR>Om du beholder standardinstillingen vil du kunne \r\nlegge in spørsmål direkte etter at Undersøkelsen er lagt til.</P>', 1068760658);
