insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,12,'MessageBoard','<P>Meldinger, også kalt Forum eller Diskusjonsgrupper, er en super måte å slippe \r\ntil et brukermiljø til en WEB-side eller intranet. Mange firma bruker meldinger \r\nintert for sammarbeid om prosjekter.</P>', 1068759015);
