insert into international (internationalId,languageId,namespace,message,lastUpdated) values (79,12,'MessageBoard','<P>En Meldingstavle kan inneholde et eller flere Forum. De følgende \r\ninnstillinger er koplet til hvert forum.</P>\r\n<P><STRONG>Tittel</STRONG><BR>Tittelen til Forumet</P>\r\n<P><STRONG>Beskrivelse</STRONG><BR>Beskrivelse av forumet</P>\r\n<P><STRONG>Merk:</STRONG> Alle innstillinger for forum gjelder også. Se egen \r\nhjelpeside for dette.</P>', 1068757258);
