insert into international (internationalId,languageId,namespace,message,lastUpdated) values (97,2,'EventsCalendar','<p>Nachfolgend eine Liste von Template Variablen für Ereignis Templates.</p>\r\n<p><b>title</b><br>\r\nDer Titel des Ereignisses.</p>\r\n<p><b>start.label</b><br>\r\nDie &nbsp;übersetzte Bezeichnung des Startdatums.</p>\r\n<p><b>start.date</b><br>\r\nDas Datum, an dem das Ereignis beginnt.</p>\r\n<p><b>end.date</b><br>\r\nDas Datum, an dem dieses Ereignis endet.</p>\r\n<p><b>end.label</b><br>\r\nDie übersetzte Bezeichung des Ende Datums.</p>\r\n<p><b>canEdit</b><br>\r\nVariable, falls der Benutzer des Ereignis bearbeiten kann.</p>\r\n<p><b>edit.url</b><br>\r\nDie URL zum Bearbeiten dieses Ereignisses.</p>\r\n<p><b>edit.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL (z. B. \'Termin bearbeiten\').</p>\r\n<p><b>delete.url</b><br>\r\nDie URL zum Löschen dieses Ereignisses.</p>\r\n<p><b>delete.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>previous.url</b><br>\r\nDie URL, um sich das vorherige Ereignis anzeigen zu lassen.</p>\r\n<p><b>previous.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>next.url</b><br>\r\nDie URL, um sich das nächste Ereignis anzeigen zu lassen.</p>\r\n<p><b>next.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>description</b><br>\r\nDie Beschreibung für dieses Ereignis.</p>', 1067291863); 
