insert into international (internationalId,languageId,namespace,message,lastUpdated) values (39,2,'Product','<p>WebGUI enthält eine Produktverwaltung, mit dem Sie Produkte und Services schnell und einfach zu Ihrer Seite hinzufügen können.</p>\r\n<p><b>Template</b><br>\r\nWählen Sie ein Layout für dieses Produkt.</p>\r\n<p><b>Preis</b><br>\r\nHier können Sie einen Preis für dieses Produkt eingeben. Sie können aber auch einen&nbsp;Text angeben, wie \"Rufen Sie uns an, wenn Sie den Preis erfahren möchten\" oder lassen Sie dieses Feld einfach leer.</p>\r\n<p><b>Produkt Nummer</b><br>\r\nDie Produkt Nummber, SKU, ISBN oder ähnliches für dieses Produkt.</p>\r\n<p><b>Produkt Grafik 1</b><br>\r\nEine Grafik für dieses Produkt.</p>\r\n<p><b>Produkt Grafik 2</b><br>\r\nEine weitere Grafik für dieses Produkt.</p>\r\n<p><b>Produkt Grafik 3</b><br>\r\nEine dritte Grafik für dieses Produkt.</p>\r\n<p><b>Prospekt/Broschüre</b><br>\r\nEin Prospekt oder eine Broschüre für dieses Produkt.</p>\r\n<p><b>Handbuch</b><br>\r\nDas Produkt-, Benutzer oder Servicehandbuch für dieses Produkt.</p>\r\n<p><b>Garantie</b><br>\r\nDie Garantie für dieses Produkt.</p>', 1067291738); 
