insert into international (internationalId,languageId,namespace,message,lastUpdated) values (95,2,'EventsCalendar','<p>Die nachfolgenden Templatevariablen sind verfügbar, um Ihren Veranstaltungskalender anzupassen.</p>\r\n<p><b>addevent.url</b><br>\r\nDie URL, um ein Ereignis zum Kalender hinzuzufügen.</p>\r\n<p><b>addevent.label</b><br>\r\nDer übersetzte Text zum Hinzufügen eines Ereignisses (z. B. \'Neue Veranstaltung hinzufügen\').</p>\r\n<p><b>calendar.big</b><br>\r\nEine komplette&nbsp;Kalenderansicht. Wochentage werden komplett ausgeschrieben (Montag, Dienstag, ...).</p>\r\n<p><b>calendar.small</b><br>\r\nEine kleinere Kalenderansicht mit abgekürzten Wochentagen (z. B. M, D, M, D ...).</p>\r\n<p><b>calendar.firstPage</b><br>\r\nEin Link zur ersten Seite im Kalender.</p>\r\n<p><b>calendar.lastPage</b><br>\r\nEin Link zur letzten Seite im Kalender.</p>\r\n<p><b>calendar.nextPage</b><br>\r\nEin Link zur nächsten Seite im Kalender.</p>\r\n<p><b>calendar.previousPage</b><br>\r\nEin Link zur vorherigen Seite im Kalender.</p>\r\n<p><b>calendar.pageList</b><br>\r\nEine Liste aller Seiten des Kalenders (z. b. Seite 1, 2, 3, ....).</p>\r\n<p><b>calendar.multiplePages</b><br>\r\nEine bedingte Variable, die anzeigt, ob es mehr als eine Seite im Kalender gibt..</p>\r\n<p><b>list_loop</b><br>\r\nEine Schleife (Loop), die die Listenansicht eines Kalenders umschliesst.</p>\r\n<blockquote><b>list.date</b><br>\r\nDas Datum dieses Ereignisses. \r\n<p><b>list.title</b><br>\r\nDer Titel dieses Ereignisses.</p>\r\n<p><b>list.description</b><br>\r\nDie Beschreibung dieses Ereignisses.</p>\r\n<p><b>list.sameAsPrevious</b><br>\r\nEine Bedingungs-Variable, falls dieses Ereignis das selbe Datum wie ein anderes/das letzte&nbsp;Ereignis hat.</p>\r\n<p><b>list.url</b><br>\r\nDie URL zur Komplettansicht dieses Ereignisses.</p>\r\n<p><b>list.controls</b><br>\r\nDie WebGUI Verwaltungscontrols für dieses Ereignis (z. B. Ereingis bearbeiten).</p>\r\n</blockquote>\r\n<p><b>list.firstPage</b><br>\r\nEin Link zur ersten Seite des Kalenders (inder Listenansicht).</p>\r\n<p><b>list.lastPage</b><br>\r\nEin L ink zur letzten Seite des Kalenders (in der Listenansicht).</p>\r\n<p><b>list.nextPage</b><br>\r\n Ein Link zur nächsten Seite des Kalenders (in der Listenansicht).</p>\r\n<p><b>list.previousPage</b><br>\r\n Ein Link zur vorherigen Seite des Kalenders (in der Listenansicht).</p>\r\n<p><b>list.pageList</b><br>\r\n Ein Link zu allen Seiten des Kalenders (in der Listenansicht).</p>\r\n<p><b>list.multiplePages</b><br>\r\nEine Bedingungs-Variable, falls es mehr als eine Seite im Kalender gibt.</p>', 1067291653); 
