insert into international (internationalId,languageId,namespace,message,lastUpdated) values (63,2,'Product','<p>Nachfolgend eine Liste der Template Variablen, die in Produkt Templates verfügbar sind.</p>\r\n<p><b>brochure.icon</b><br>\r\nDie URL zum Icon des Prospekts.</p>\r\n<p><b>brochure.url</b><br>\r\nDie URL um dieses Prospekt zu downloaden.</p>\r\n<p><b>brochure.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>warranty.icon</b><br>\r\nDie URL zum Icon der Garantie.</p>\r\n<p><b>warranty.url</b><br>\r\nDie URL um die Garantie zu downloaden.</p>\r\n<p><b>warranty.label</b><br>\r\nDie Bezeichnung für diese URL.</p>\r\n<p><b>image1</b><br>\r\nDie URL zur Produktgrafik 1.</p>\r\n<p><b>thumbnail1</b><br>\r\nDie URL zum Vorschaubild der Produktgrafik 1.</p>\r\n<p><b>image2</b><br>\r\nDie URL zur Produktgrafik 2.</p>\r\n<p><b>thumbnail2</b><br>\r\nDie URL zum Vorschaubild der Produktgrafik 2.</p>\r\n<p><b>image3</b><br>\r\nDie URL zur Produktgrafik 3.</p>\r\n<p><b>thumbnail3</b><br>\r\nDie URL zum Vorschaubild der Produktgrafik 3.</p>\r\n<p><b>addfeature.url</b><br>\r\nDie URL um eine Produkteigenschaft (Feature) hinzuzufügen.</p>\r\n<p><b>addfeature.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>feature_loop</b><br>\r\nDiese Schleife enthält alle Produkteigenschaften, die zu diesem Produkt gehören.</p>\r\n<blockquote><b>feature.controls</b><br>\r\n WebGUI Verwaltungselemente für diese Eigenschaft. \r\n<p><b>feature.feature</b><br>\r\nDer Text der Produkteigenschaft.</p>\r\n</blockquote>\r\n<p><b>addbenefit.url</b><br>\r\nDie URL um Vorteile hinzuzufügen.</p>\r\n<p><b>addbenefit.label</b><br>\r\nDie übersetzte Bezeichnung &nbsp;für diese URL.</p>\r\n<p><b>benefit_loop</b><br>\r\nDiese Schleife enthält alle Vorteile dieses Produktes.</p>\r\n<blockquote><b>benefit.benefit</b><br>\r\nDer Vorteil selbst (Text des Vorteils). \r\n<p><b>benefit.controls</b><br>\r\nWebGUI Verwaltungselemente für diesen Vorteil.</p>\r\n</blockquote>\r\n<p><b>addspecification.url</b><br>\r\nDie URL um Merkmale hinzuzufügen.</p>\r\n<p><b>addspecification.label</b><br>\r\nDie übersetzte Bezeichnung für diese URL.</p>\r\n<p><b>specification_loop</b><br>\r\nEine Liste von Merkmalen zu diesem Produkt.</p>\r\n<blockquote><b>specification.controls</b><br>\r\nWebGUI Verwaltungselemente für dieses Merkmal. \r\n<p><b>specification.specification</b><br>\r\nDer Text des Merkmals selbst.</p>\r\n<p><b>specification.units</b><br>\r\nDie Einheiten für dieses Merkmal, z.B. Zentimeter</p>\r\n<p><b>specification.label</b><br>\r\nDie Bezeichnung für dieses Merkmal, z. B. Höhe</p>\r\n</blockquote>\r\n<p><b>addaccessory.url</b><br>\r\nDie URL zum Hinzufügen von Zubehör.</p>\r\n<p><b>addaccessory.label</b><br>\r\nDie übersetzte Bezeichnung &nbsp;für diese URL.</p>\r\n<p><b>accessory_loop</b><br>\r\nDiese Schleife enthält all das&nbsp;zu diesem Produkt verbundene Zubehör.</p>\r\n<blockquote><b>accessory.url</b><br>\r\nDie URL zum verbundenen Zubehör. \r\n<p><b>accessory.title</b><br>\r\nDer Titel des verbundenen Zubehörs.</p>\r\n<p><b>accessory.controls</b><br>\r\nWebGUI Verwaltungselemente für dieses Zubehör.</p>\r\n</blockquote>\r\n<p><b>addRelatedProduct.url<br>\r\n</b>Die URL um ein verwandtes Produkt hinzuzufügen.</p>\r\n<p><b><b>addRelatedProduct.label</b><br>\r\n</b>Die übersetzte Bezeichnung &nbsp;für diese URL.</p>\r\n<p><b><b>relatedproduct.url</b><br>\r\n</b>Die URL zu diesem verwandten Produkt.</p>\r\n<p><b><b>relatedproduct.title</b><br>\r\n</b>Der Titel des verwandten Produkts.</p>\r\n<p><b><b>relatedproduct.controls</b><br>\r\n</b>WebGUI Verwaltungselemente für dieses verwandte Produkt.</p>', 1067291164); 
