insert into international (internationalId,languageId,namespace,message,lastUpdated) values (76,2,'FileManager','<p>Dies ist eine Liste von Template Variablen, die Sie in Downloadmanager Templates benutzen können.</p>\r\n<p><b>titleColumn.url</b><br>\r\nDie URL, um nach dem Titel zu sortieren.</p>\r\n<p><b>titleColumn.label</b><br>\r\nDie übersetzte Bezeichnung des Titels.</p>\r\n<p><b>descriptionColumn.url</b><br>\r\nDie URL, um nach der Beschreibung zu sortieren.</p>\r\n<p><b>descriptionColumn.label</b><br>\r\nDie übersetzte Bezeichnung für die Beschreibung.</p>\r\n<p><b>dateColumn.url</b><br>\r\nDie URL, um nach dem Upload-Datum zu sortieren.</p>\r\n<p><b>dateColumn.label</b><br>\r\nDie übersetzte Bezeichnung für das Upload-Datum.</p>\r\n<p><b>search.form</b><br>\r\nWebGUI\'s Power Suchformular.</p>\r\n<p><b>search.url</b><br>\r\nDie URL, um den Suchmodus an- oder abzuschalten.</p>\r\n<p><b>search.label</b><br>\r\nDie übersetzte Bezeichnung für den Suchlink.</p>\r\n<p><b>addfile.url</b><br>\r\nDie URL, um eine Datei hinzuzufügen.</p>\r\n<p><b>addfile.label</b><br>\r\nDie übersetzte Bezeichnung für diesen Link.</p>\r\n<p><b>file_loop</b><br>\r\nEine Schleife, die Informationen über jede upgeloadete Datei enthält.</p>\r\n<blockquote><b>file.canView</b><br>\r\nEine Bedingungsvariable, ob der Benutzer die Berechtigung hat, diese Datei anzuschauen. \r\n<p><b>file.controls</b><br>\r\nDie WebGUI Verwaltungscontrols für diese Datei.</p>\r\n<p><b>file.title</b><br>\r\nDer Dateititel.</p>\r\n<p><b>file.version1.name</b><br>\r\nDer Dateiname der ersten Version dieser Datei.</p>\r\n<p><b>file.version1.url</b><br>\r\nDie Download-URL für diese erste Version der Datei.</p>\r\n<p><b>file.version1.icon</b><br>\r\nDie URL zum Dateityp-Icon für die erste Version der Datei.</p>\r\n<p><b>file.version1.size</b><br>\r\nDie Dateigrösse der ersten Version der Datei.</p>\r\n<p><b>file.version1.type</b><br>\r\nDer Dateityp (siehe auch file.version1.icon).</p>\r\n<p><b>file.version1.thumbnail</b><br>\r\nDie URL zum Vorschaubild (Thumbnail)für die erste Version dieser Datei.</p>\r\n<p><b>file.version1.isImage</b><br>\r\nEine V ariable um zu ermitteln, ob diese Datei eine Grafik ist oder nicht.</p>\r\n<p><b>file.version2.name</b><br>\r\nDer Dateiname für die Alternative #1 dieser Datei.</p>\r\n<p><b>file.version2.url</b><br>\r\nDie Download-URL für Alternative #1.</p>\r\n<p><b>file.version2.icon</b><br>\r\nDie URL zum Dateityp-Icon für Alternative #1.</p>\r\n<p><b>file.version2.size</b><br>\r\nDie Dateigrösse für Alternative #1.</p>\r\n<p><b>file.version2.type</b><br>\r\nDer Dateityp von Alternative #1.</p>\r\n<p><b>file.version2.thumbnail</b><br>\r\nDie URL zum Vorschaubild (Thumbnail) für Alternative #1.</p>\r\n<p><b>file.version2.isImage</b><br>\r\nEine Variable um zu ermitteln, ob es sich bei Alternative #1 um eine Grafik handelt oder nicht.</p>\r\n<p><b>file.version3.name</b><br>\r\nDer Dateiname für die Alternative #2 dieser Datei.</p>\r\n<p><b>file.version3.url</b><br>\r\nDie Download-URL für Alternative #2.</p>\r\n<p><b>file.version3.icon</b><br>\r\nDie URL zum Dateityp-Icon für Alternative #2.</p>\r\n<p><b>file.version3.size</b><br>\r\nDie Dateigrösse für Alternative #2.</p>\r\n<p><b>file.version3.type</b><br>\r\nDer Dateityp von Alternative #2.</p>\r\n<p><b>file.version3.thumbnail</b><br>\r\nDie URL zum Vorschaubild (Thumbnail) für Alternative #2.</p>\r\n<p><b>file.version3.isImage</b><br>\r\nEine Variable um zu ermitteln, ob es sich bei Alternative #2 um eine Grafik handelt oder nicht.</p>\r\n<p><b>file.description</b><br>\r\nDie Beschreibung für die Datei (für alle 3 Versionen gleich).</p>\r\n<p><b>file.date</b><br>\r\nDas Datum, an dem die Datei(en) hochgeladen wurde(n).</p>\r\n<p><b>file.time</b><br>\r\nDie Uhrzeit, an dem die Datei(en) hochgeladen wurde(n).</p>\r\n</blockquote>\r\n<p><b>noresults.message</b><br>\r\nEine übersetzte Nachricht, dass es keine Dateien zum Downloaden für diesen Benutzer gibt.</p>\r\n<p><b>noresults</b><br>\r\nEine Variable, um zu ermitteln, ob es Dateien für diesen Benutzer zum Downloaden gibt.</p>\r\n<p><b>firstPage</b><br>\r\n Ein Link zur ersten Seite.</p>\r\n<p><b>lastPage</b><br>\r\nEin Link zur letzten Seite.</p>\r\n<p><b>nextPage</b><br>\r\nEin Link zur nächst en Seite.</p>\r\n<p><b>previousPage</b><br>\r\nEin Link zur vorherigen Seite.</p>\r\n<p><b>pageList</b><br>\r\nEine Liste aller Seiten.</p>\r\n<p><b>multiplePages</b><br>\r\nEine Variable um zu ermitteln, ob es mehrere Seiten gibt.</p>', 1067290895); 
