insert into international (internationalId,languageId,namespace,message,lastUpdated) values (846,2,'WebGUI','<p>Diese Makros werden hauptsächlich benötigt um Styles in WebGUI zu erstellen.</p>\r\n\r\n<p><b>^AdminBar;</b><br>\r\nPlatziert die Administrationsleiste auf der Seite. Dieses Element wird im \"Body\" Bereich benötigt..</p>\r\n\r\n<p><b>^c; - Company Name</b><br>\r\nDer Firmenname, den Sie in den Profileinstellungen des Administrators eingetragen haben.</p>\r\n\r\n<p><b>^e; - Company Email Address</b><br>\r\nDie Emailadresse, die Sie in den Profileinstellungen des Administrators eingetragen haben.</p>\r\n\r\n<p><b>^Extras;</b><br>\r\nGibt den Pfad zum WebGUI \"extras\" Verzeichnis zurück, der Dinge wie die WebGUI Icons enthält.</p>\r\n\r\n<p><b>^LastModified; oder ^LastModified();</b><br>\r\nZeigt das Datum an, an dem die Seite zuletzt bearbeitet wurde, basierend auf den Wobjects dieser Seite. Das Datum wird standardmässig in dem Format dargestellt, wie esder Benutzer in seinem Profil ausgewählt hat. Optinal kann man 2 Parameter angeben: Der Text, der vor dem Datum angezeigt werden soll und zweitens der Formatierungs-String für das Datum. (für mehr Details schauen Sie sich das D (Datum) Makro an.</p>\r\n\r\n<p><i>Beispiel:</i> ^LastModified(\"Letzte Bearbeitung: \",\"%c %D, %y\");</p>\r\n\r\n<p><b>^PageTitle;</b><br>\r\nZeigt den Titel der aktuellen Seite an.</p>\r\n\r\n<p><b>^r; or ^r(); - Druckversion der Seite</b><br>\r\nErstellt einen Link, um den Style von der Seite zu entfernen bzw. um Sie für den Ausdruck zu optimieren. Zusätzlich können Sie &nbsp;den Linktext ändern, wenn Sie ein Makro wie dieses einsetzen: \r\n\r\n<b>^r(\"Drucken!\");</b>.</p>\r\n\r\n<p>Standardmässig wird, wenn der Link angeklickt wird, der aktuelle Seiten Style ersetzt mit dem \"Make Page Printable\" Style im Style Manager. Genauso gut kann dies aber geändert bzw. überschrieben werden, wenn Sie den Namen eines anderen Styles als zweiten Parameter angeben, wie z. B. ^r(\"Print!\",\"Demostyle\");</p>\r\n\r\n<p><i>Beachte:</i> Sie können auch im speziellen Fall ^r(linkonly); benutzen, um lediglich die URL zur Druckversion angeben und nichts weiter.<br>\r\nDie Style Sheet Klasse \'\r\n\r\n<b>.makePrintableLink</b>\' ist diesem Makro zugeordnet.</p>\r\n\r\n<p><b>^RootTitle;</b><br>\r\nGibt den Titel der aktuellen Startseite (Root) aus. Wenn z. B. der Titel der aktuellen Startseite \"Home\" ist, wird dieser Titel angezeigt. Einige Seiten haben mehrere Startseiten (Roots) und folglich wird etwas benötigt, um dem Besucher zu zeigen, in welchem Bereich er sich gerade befindet.</p>\r\n\r\n<p><b>^u; - Company URL</b><br>\r\nDie URL, die Sie in den Profileinstellungen des Administrators eingetragen haben.</p>\r\n\r\n<p><b>^?; - Search</b><br>\r\nFügt ein Suchformular ein. Dieses ist an die eingebaute WebGUI Suchmaschine angebunden..</p>\r\n\r\n<p><i>Beachte:</i> Die Style Sheet Klasse \'\r\n\r\n<b>.searchBox</b>\' ist! diesem Makro zugeordnet.</p>\r\n\r\n<p><b>^Spacer();</b><br>\r\nErstellt einen Abstand in Ihrem Layout. Sie können 2 Parameter angeben: Breite und Höhe.</p>\r\n\r\n<p><i>Beispiel:</i> ^Spacer(\"100\",\"50\");</p>\r\n\r\n<p><b>^-;</b><br>\r\nDies ist das \"Trenn-Makro\", d. h. es darf genau einmal in jedem Style auftauchen, da es angibt, wo der Content/Inhalt für die Seite eingefügt werden soll.</p>', 1067290418); 
