insert into international (internationalId,languageId,namespace,message,lastUpdated) values (632,2,'WebGUI','<p>Wobjects können Sie hinzufügen, indem Sie aus dem Menü &quot;Inhalt hinzufügen ...&quot;, das sich in der Regel oben rechts befindet, ein beliebiges Wobject auswählen. Sie können es bearbeiten, wenn Sie den Bearbeiten-Button anklicken, der immer über einem Wobject steht, das Sie in die Seite eingebunden haben.</p>\r\n\r\n<p>Einige Eigenschaften finden Sie bei fast allen Wobjects. Diese wären:</p>\r\n\r\n<p><B>Wobject ID</B><br>\r\nDies ist das eindeutige Kennzeichen das WebGUI benutzt, um diese Wobject Instanz zu identifizieren. Normale Benutzer sollten eigentlich niemals mit dieser Wobject ID konfrontiert werden, aber einige erfahrene Benutzer sollten sie schon kennen, da sie z.B. hilfreich in den SQL Reports sind.</p>\r\n\r\n<p><B>Titel</B><br>\r\nDer Titel des Wobjects. Typischerweise wird dieser im Kopfbereich eines jeden Wobjects angezeigt.</p>\r\n\r\n<p><I>Beachte:</I> Sie sollten immer einen Titel vergeben, es sei denn, sie schalten die Anzeige des Titels ab (siehe nächste Einstellung). Und zwar sollten Sie das u. a. deswegen tun, da dieser Titel auch im Mülleimer oder in der Zwischenablage angezeigt wird. So können Sie immer auf Anhieb sehen, um welches Wobject es sich eigentlich handelt.</p>\r\n\r\n<p><B>Titel anzeigen?</B><br>\r\nMöchten Sie, dass der Titel angezeigt wird?</p>\r\n\r\n<p><B>Makros ausführen?</B><br>\r\nMöchten Sie, dass Makros, die Sie im Inhaltsbereich schreiben, auch ausgeführt werden? Manchmal möchten Sie dies zulassen, aber oft besteht keine Notwendigkeit dafür. Wenn Sie das Ausführen von Makros ausschalten, werden Ihre Seiten auch ein wenig schneller aufgebaut.</p>\r\n\r\n<p><B>Position des Templates</B><br>\r\nTemplate Positionen gehen von 0 (Null) bis zu beliebigen Nummern. Wie viele hier möglich sind, ist abhängig von dem Template, dass dieser Seite zugeordnet ist. Das StandardTemplate bietet nur eine Template Position, andere können mehr anbieten. Wenn Sie also eine Template Position auswählen, bestimmen Sie, wo das Wobject innerhalb der Seite erscheinen soll.</p>\r\n\r\n<p><B>Anfangsdatum</B><br>\r\nAb welchem Datum soll das Wobject auf der Seite erscheinen? Vor diesem Zeitpunkt ist es dann nur für Content Manager sichtbar.</p>\r\n\r\n<p><B>Ende Datum</B><br>\r\nBis zu welchem Datum soll das Wobject angezeigt werden? Nach diesem Zeitpunkt ist es dann nur für Content Manager sichtbar.</p>\r\n\r\n<p><B>Besitzer</B><br>\r\nDer Besitzer eines Wobjects ist gewöhnlich die Person, die das Wobject erstellt hat. Dieser Benutzer hat die vollen Bearbeitungs- und Anschaurechte, es sei denn, die Berechtigungen der Seite, auf der das Wobject sich befindet, gewähren diesem Benutzer keine Rechte.</p>\r\n\r\n<p><B>Beachte:</B> Der Besitzer kann nur von einem Administrator geändert werden.</p>\r\n\r\n<p><B>Wer kann&nbsp;anschauen?</B><br>\r\nWählen Sie die Gruppe aus, die dieses Wobject anschauen darf. Wenn Sie möchten, dass sowohl Besucher als auch registrierte Benutzer dies dürfen, wählen Sie &quot;Jeder&quot; aus.</p>\r\n\r\n<p><B>Wer kann bearbeiten?</B><br>\r\nWählen Sie die Gruppe aus, die dieses Wobject bearbeiten darf. Diese Gruppe hat dann auch die Berechtigung, das Wobject anzuschauen.</p>\r\n\r\n<p><B>Inhalt/Beschreibung</B><br>\r\nEin Inhaltsbereich, in den Sie soviel Text schreiben können, wie Sie möchten. Wenn Sie z. B. eine FAQ hinzufügen, können Sie hier eine Zusammenfassung hineinschreiben, um zu definieren, um welche FAQ es sich handelt. Wenn Sie das Wobject &quot;Artikel&quot; einfügen, schreiben Sie hier den eigentlichen Text des Artikels, also den Inhalt, hinein.</p>', 1067289682);
