insert into international (internationalId,languageId,namespace,message,lastUpdated) values (32,2,'PhotoGallery','<p>Hiermit können Sie Grafiken aus dem Uploadmanager zur Fotogallerie hinzufügen. Dargestellt wird der Upload-Baum und Sie können hier eins oder mehrere Grafiken hinzufügen oder das hochgeladene Bild bearbeiten (natürlich nicht die Grafik selber, sondern die Datei)</p>\r\n\r\n<p><strong>Images<br>\r\n</strong>Wählen Sie die Grafik(en) nach Namen aus dem UploadManager aus, die Sie zur Fotogallerie hinzufügen möchten. Bei Verwendung der \"CTRL\" oder \"STRG\"- Taste können Sie auch mehrere auswählen.</p>\r\n\r\n<p><strong>Breite des Vorschaubildes<br>\r\n</strong>Geben Sie die Grösse in Pixeln an, um die Höhe des Vorschaubildes zu definieren. Wenn Sie hier \'0\' angeben, wird die Standardgrösse verwendet, die Sie in den Systemeinstellungen verwenden</p>\r\n\r\n<p><strong>Höhe des Vorschaubildes<br>\r\n</strong>Geben Sie die Grösse in Pixeln an, um die Breite des Vorschaubildes zu definieren. Wenn Sie hier \'0\' angeben, wird die Standardgrösse verwendet, die Sie in den Systemeinstellungen verwenden</p>\r\n\r\n<p><strong>Beschreibungstext<br>\r\n</strong>You may type a small description of your image to be displayed with it.&nbsp; If no cutline is entered, none will be displayed<br>\r\n</p>', 1067289072); 
