insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'MessageBoard','<p>Diskussionsforen, auch genannt Foren, Diskussionen oder Messageboards, sind \r\neine sehr gute Möglichkeit der Interaktion mit Ihren Besuchern. Diese können \r\ndamit über verschiedene Themen diskutieren. Einige Firmen benutzen diese Foren z. B. auch in internen \r\nProjekten.</p>', 1067274751);
