insert into international (internationalId,languageId,namespace,message,lastUpdated) values (71,2,'Article','<p>Artikel sind das &quot;Schweizer Messer&quot; von WebGUI. Der grösste Teil von \r\nstatischem Inhalt kann mit Hilfe des Artikels hinzugefügt werden.</p>\r\n<p>Beachte: Sie können einen mehrseitigen Artikel erstellen, in dem Sie das Seperator Makro (^-;) an verschiedenen Stellen in Ihrem Artikel einbinden.<br>\r\n<br>\r\n<B>Template</B><br>\r\nWählen Sie ein Template für Ihren Artikel aus.</p>\r\n\r\n<p><B>Grafik</B><br>\r\nWählen Sie eine Grafik (.jpg, .gif, .png) von Ihrem lokalen Laufwerk aus. Diese Datei wird zum Server hochgeladen und erscheint in Ihrem Artikel.<br>\r\n<br>\r\n<B>Dateianhang</B><br>\r\nMöchten Sie z. B. ein Word-Dokument, ein Zip-File oder irgendeine andere Datei zum Download anbieten, so können Sie diese von Ihrem lokalen Laufwerk auswählen und hochladen.<br>\r\n<br>\r\n<B>Link Titel</B><br>\r\nMöchten Sie einen Link zu Ihrem Artikel hinzufügen, so geben Sie hier den Link-Titel ein.<br>\r\n<br>\r\n<I>Beispiel:</I> Plainblack<br>\r\n<br>\r\n<B>Link URL</B><br>\r\nWenn Sie zuvor einen Link Titel eingeben haben, so geben Sie hier die URL ein.<br>\r\n<br>\r\n<I>Beispiel:</I> http://www.plainblack.com<br>\r\n<br>\r\n<B>Carriage Return beachten?</B><br>\r\nWenn Sie HTML-Code eingeben, so brauchen Sie diese Option nicht zu aktivieren. Aber wenn Sie keinen HTML-Code, sondern einfachen Text eingeben, und Sie möchten, dass auch ein &quot;Carriage Return&quot;, also ein Zeilenumbruch, eingefügt wird, so aktivieren Sie diese Option. Andernfalls werden die Zeilenumbrüche, die Sie hier eingeben, nicht berücksichtigt.</p>\r\n\r\n<p><B>Diskussion erlauben?</B><br>\r\nWählen Sie diese Option, wenn Sie Kommentare der Besucher oder Benutzer zu diesem Artikel erlauben möchten, so wie es z. B. auf <a href=\"http://www.slashdot.org\">http://www.slashdot.org</a> möglich ist.</p>', 1067273948); 
