insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1092,3,'WebGUI','<p>Het volgende is een lijst met variabelen die beschikbaar zijn in dit sjabloon:</p>\r\n<p><b>title</b><br>\r\nDe standaard titel van dit verslag.</p>\r\n<p><b>answer.label</b><br>\r\nHet standaard label voor answer_loop.</p>\r\n<p><b>response.count.label</b><br>\r\nHet standaard label voor response.count.</p>\r\n<p><b>response.percent.label</b><br>\r\nHet standaard label voor response.percent.</p>\r\n<p><b>show.responses.label</b><br>\r\nHet standaard label dat reacties weergeeft.</p>\r\n<p><b>show.comments.label</b><br>\r\nHet standaard label dat commentaar weergeeft.</p>\r\n<p><b>question_loop</b><br>\r\nEen loop die vragen bevat.</p>\r\n<blockquote>\r\n<p><b>question</b><br>\r\nDe vraag zelf.</p>\r\n<p><b>question.id</b><br>\r\nDe unieke identificator van deze vraag.</p>\r\n<p><b>question.isRadioList</b><br>\r\nEen boolean die aangeeft of het antwoord op deze vraag een radio lijst is.</p>\r\n<p><b>question.response.total</b><br>\r\nHet totale aantal reacties op dit antwoord.</p>\r\n<p><b>question.allowComment</b><br>\r\nEen boolean die aangeeft of commentaar op deze vraag is toegestaan.</p>\r\n<p><b>answer_loop</b><br>\r\nEen loop die de antwoorden en reacties op deze vraag bevat.</p>\r\n<blockquote>\r\n<p><b>answer.isCorrect</b><br>\r\nEen boolean die aangeeft of dit antwoord als correct is aangeduid.</p>\r\n<p><b>answer</b><br>\r\nHet antwoord zelf</p>\r\n<p><b>answer.response.count</b><br>\r\nHet totale aantal reacties dat gegeven is op dit antwoord.</p>\r\n<p><b>answer.response.percent</b><br>\r\nHet percentage reacties dat gegeven is op dit antwoord.</p>\r\n<p><b>comment_loop</b><br>\r\nEen loop die al het commentaar op dit antwoord bevat.</p>\r\n<blockquote>\r\n<p><b>answer.comment</b><br>\r\nEen commentaar.</p>\r\n</blockquote>\r\n</blockquote>\r\n</blockquote>', 1078761321);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1090,3,'WebGUI','<p>Het volgende is een lijst met de variabelen die beschikbaar zijn voor het weergeven van een enkele reactie.</p>\r\n<p><b>title</b><br>\r\nDe standaard titel voor dit verslag.</p>\r\n<p><b>delete.url</b><br>\r\nHet URL voor het verwijderen van deze reactie.</p>\r\n<p><b>delete.label</b><br>\r\nHet standaard label voor delete.url.</p>\r\n<p><b>start.date.label</b><br>\r\nHet standaard label voor start.date.human.\r\n<p><b>start.date.epoch</b><br>\r\nDe epoch representatie van het tijdsstip waarop deze gebruiker begon met de reactie op de enqu&ecirc;te.</p>\r\n<p><b>start.date.human</b><br>\r\nDe normaal leesbare representatie van de datum waarop deze gebruiker begon met de reactie op de enqu&ecirc;te.</p>\r\n<p><b>start.time.human</b><br>\r\nDe normaal leesbare representatie van de tijd waarop deze gebruiker begon met de reactie op de enqu&ecirc;te.</p>\r\n<p><b>end.date.label</b><br>\r\nHet standaard label voor end.date.human.</p>\r\n<p><b>end.date.epoch</b><br>\r\nDe epoch representatie van het tijdsstip waarop deze gebruiker de reactie op de enqu&ecirc;te be&euml;indigde.</p>\r\n<p><b>end.date.human</b><br>\r\nDe normaal leesbare representatie van de datum waarop deze gebruiker de reactie op de enqu&ecirc;te af had.</p>\r\n<p><b>end.time.human</b><br>\r\nDe normaal leesbare representatie van de tijd waarop deze gebruiker de reactie op de enqu&ecirc;te af had</p>\r\n<p><b>duration.label</b><br>\r\nHet standaard label voor de duur.</p>\r\n<p><b>duration.minutes</b><br>\r\nHet aantal minuten dat het kostte om deze enqu&ecirc;te volledig in te vullen.</p>\r\n<p><b>duration.minutes.label</b><br>\r\nEen label voor \"minuten\".</p>\r\n<p><b>duration.seconds</b><br>\r\nDe overblijvende seconden van de berekening van duration.minutes.</p>\r\n<p><b>duration.seconds.label</b><br>\r\nEen label voor \"seconden\".\r\n<p><b>answer.label</b><br>\r\nHet standaard label voor question.answer.</p>\r\n<p><b>response.label</b><br>\r\nHet standaard label voor question.label.</p>\r\n<p><b>comment.label</b><br>\r\nHet standaard label voor question.comment.</p>\r\n<p><b>question_loop</b><br>\r\nEen loop die de lijst van vragen in deze reactie laat zien.</p>\r\n<blockquote>\r\n<p><b>question</b><br>\r\nDe vraag zelf.</p>\r\n<p><b>question.id</b><br>\r\nDe unieke identificator van deze vraag.</p>\r\n<p><b>question.isRadioList</b><br>\r\nEen boolean die aangeeft of de antwoorden op deze vraag een radio lijst zijn.</p>\r\n<p><b>question.response</b><br>\r\nDe reactie van de gebruiker op deze vraag.</p>\r\n<p><b>question.comment</b><br>\r\nHet eventuele commentaar van de gebruiker op deze vraag.</p>\r\n<p><b>question.isCorrect</b><br>\r\nEen boolean die aangeeft of de gebruiker de vraag correct heeft beantwoord.</p>\r\n<p><b>question.answer</b><br>\r\nHet correcte antwoord op deze vraag.</p>\r\n</blockquote>', 1078760440);
insert into international (internationalId,languageId,namespace,message,lastUpdated) values (1088,3,'WebGUI','<p>De volgende sjabloon variabelen zijn beschikbaar in het waarderingsoverzicht verslag van de enqu&ecirc;te.</p>\r\n<p><b>title</b><br>\r\nDe standaard titel van het verslag.</p>\r\n<p><b>question.count</b><br>\r\nHet aantal vragen in de enqu&ecirc;te.</p>\r\n<p><b>response.user.label</b><br>\r\nHet standaard label voor response.user.name.</p>\r\n<p><b>response.count.label</b><br>\r\nHet standaard label voor response.count.correct.</p>\r\n<p><b>response.percent.label</b><br>\r\nHet standaard label voor response.percent.</p>\r\n<p><b>response_loop</b><br>\r\nEen loop die een lijst met reacties bevat.</p>\r\n<blockquote>\r\n<p><b>response.url</b><br>\r\nHet URL waar deze reactie bekeken kan worden.</p>\r\n<p><b>response.user.name</b><br>\r\nDe gebruikersnaam van de gebruiker die deze reactie heeft gegeven.</p>\r\n<p><b>response.count.correct</b><br>\r\nHet totale aantal vragen dat deze gebruiker juist heeft beantwoord.</p>\r\n<p><b>response.percent</b><br>\r\nHet percentage vragen dat deze gebruiker juist heeft beantwoord.</p>\r\n</blockquote>', 1078758161);
