insert into international (internationalId,languageId,namespace,message,lastUpdated) values (28,3,'IndexedSearch','<p>Dit is de lijst met sjabloon variabelen, die beschikbaar zijn voor zoek sjablonen:</p>\r\n<p><b>query</b><br>\r\nBevat de waarde van de <i>query</i> formulier variabele.<br>\r\nDe <i>allWords, atLeastOne, exactPhrase</i> en <i>without</i> waarden zijn verbonden aan deze variabele.</p>\r\n<p><b>queryHighlighted</b><br>\r\nHetzelfde als <b>query</b> maar dan gehighlight.</p>\r\n<p><b>allWords</b><br>\r\nBevat de waarde van de <i>allWords</i> formulier variabele.</p>\r\n<p><b>atLeastOne</b><br>\r\nBevat de waarde van de <i>atLeastOne</i> formulier variabele.</p>\r\n<p><b>exactPhrase</b><br>\r\nBevat de waarde van de <i>exactPhrase</i> formulier variabele.</p>\r\n<p><b>without</b><br>\r\nBevat de waarde van de <i>without</i> formulier variabele.</p>\r\n<p><b>duration</b><br>\r\nDe duur van het zoekproces in seconden.</p>\r\n<p><b>numberOfResults</b><br>\r\nHet aantal resultaten</p>\r\n<p><b>startNr</b><br>\r\nHet nummer van het eerste zoekresultaat op de pagina</p>\r\n<p><b>endNr</b><br>\r\nHet nummer van het laatste zoekresultaat op de pagina</p>\r\n<p><b>submit</b><br>\r\nEen knop in het formulier met erop het woord \"Zoeken\".</p>\r\n<p><b>wid</b><br>\r\nHet wobject Id van dit wobject.</p>\r\n<p><b>resultsLoop</b><br>\r\nEen loop die de zoekresultaten bevat.</p>\r\n<blockquote>\r\n<p><b>username</b><br>\r\nDe gebruikersnaam van de persoon die dit zoekresultaat heeft opgevraagd.</p>\r\n<p><b>ownerId</b><br>\r\nHet Id van de persoon die dit zoekresultaat heeft opgevraagd.</p>\r\n<p><b>userProfile</b><br>\r\nEen url van het profiel van de persoon die dit zoekresultaat heeft opgevraagd.</p>\r\n<p><b>header</b><br>\r\nDe titel van het zoekresultaat. (Dit kan het onderwerp van een bericht, de vraag van een FAQ, de titel van een Artikel, etc. zijn.)</p>\r\n<p><b>body</b><br>\r\nEen preview van de inhoud van een zoekresultaat</p>\r\n<p><b>namespace</b><br>\r\nDe namespace waarin dit zoek resultaat zich bevindt.</p>\r\n<p><b>location</b><br>\r\nHet URL van dit zoek resultaat.</p>\r\n<p><b>crumbtrail</b><br>\r\nEen kruimelspoor naar dit zoek resultaat.</p>\r\n<p><b>contentType</b><br>\r\nHet type van dit zoek resultaat.</p>\r\n</blockquote>\r\n<p>De loops <b>contentTypes, contentTypesSimple, languages, namespaces</b> en <b>users</b> zijn er allemaal hetzelfde uit. Ze kunnen gebruikt worden voor het maken van een selectie lijst, radio lijst of check lijst zodat gebruikers hun zoekopdracht kunnen verfijnen.</p>\r\n<p>Deze sjabloon variabelen zijn beschikbaar binnen de loops:</p>\r\n<blockquote>\r\n<p><b>name</b><br>\r\nDe (mogelijk internationale) naam van de optie.</p>\r\n<p><b>value</b><br>\r\nDe waarde van de optie</p>\r\n<p><b>selected</b><br>\r\nEen conditie die aangeeft of de optie wel of niet is geselecteerd.</p>\r\n</blockquote>\r\n<p><b>firstPage</b><br>\r\nEen link naar de eerste pagina in het pagina overzicht.</p>\r\n<p><b>lastPage</b><br>\r\nEen link naar de laatste pagina in het pagina overzicht.</p>\r\n<p><b>nextPage</b><br>\r\nEen link naar de volgende pagina in het pagina overzicht.</p>\r\n<p><b>previousPage</b><br>\r\nEen link naar de vorige pagina in het pagina overzicht.</p>\r\n<p><b>pageList</b><br>\r\nEen lijst met links naar alle paginas in het pagina overzicht.</p>\r\n<p><b>multiplePages</b><br>\r\nEen conditie die aangeeft of er meer dan &eacute;&eacute;n pagina is in het pagina overzicht.</p>\r\n<p><b>isFirstPage</b><br>\r\nEen conditie die aangeeft of de bezoeker de eerste pagina bekijkt.</p>\r\n<p><b>isLastPage</b><br>\r\nEen conditie die aangeeft of de bezoeker de laatste pagina bekijkt.</p>', 1078484146);
