insert into international (internationalId,languageId,namespace,message,lastUpdated) values (27,3,'IndexedSearch','<p>De Zoek-functie voegt geavanceerde zoek mogelijkheden toe aan uw WebGUI site.</p>\r\n<p><b>De te gebruiken index</b><br>\r\nDe Zoek-functie gebruikt een index voor het verkrijgen van de resultaten. Indices worden gecre&euml;erd door de scheduler. Er kan meer dan &eacute;&eacute;n index gecre&euml;erd worden. Kies hier welke index gebruikt wordt.</p>\r\n<p><b>Zoek in</b><br>\r\nStandaard worden alle paginas doorzocht. Dit kan gelimiteerd worden tot het doorzoeken van bepaalde pagina roots. Meer dan &eacute;&eacute;n keuze is toegestaan.</p>\r\n<p><b>Alleen resultaten gecre&euml;erd door</b><br>\r\nDe resultaten kunnen gelimiteerd worden tot objecten die door bepaalde gebruikers gecre&euml;erd zijn. Standaard worden objecten van elke gebruiker gegeven.</p>\r\n<p><b>Alleen resultaten van bepaalde namespaces</b><br>\r\nStandaard worden alle namespaces doorzocht. Dit kan gelimiteerd worden tot bepaalde namespaces. Een voorbeeld van het gebruik hiervan is zoeken in producten.</p>\r\n<p><b>Alleen resulaten in een bepaalde taal</b><br>\r\nAls er een meertalige site gebruikt wordt, kan deze optie de zoekresultaten limiteren tot een bepaalde taal.</p>\r\n<p><b>Alleen resultaten van type</b><br>\r\nDe zoekopdracht kan gelimiteerd worden tot bepaalde typen inhoud.</p>\r\n<p><blockquote>\r\n<i>Discussie:</i> Berichten in fora, discussies over artikelen of USS.<br>\r\n<i>Help:</i> Inhoud in het online WebGUI help systeem.<br>\r\n<i>Pagina:</i> Pagina titel en samenvatting<br>\r\n<i>Profiel:</i> Gebruikers profielen<br>\r\n<i>Wobject:</i> Wobject Titel en Beschrijving<br>\r\n<i>Details Wobject:</i> Alle andere wobject data. Bijvoorbeeld FAQ vragen, Kalender object, etc.\r\n</blockquote>\r\n</p>\r\n<p><b>Sjabloon</b><br>\r\nSelecteer een sjabloon voor de layout van de Zoek-functie. De verschillende sjablonen hebben verschillende functionaliteit.</p>\r\n<p><b>Breek pagina af na</b><br>\r\nHet aantal resultaten dat op een pagina getoond wordt.</p>\r\n<p><b>Lengte preview context</b><br>\r\nHet maximum aantal karakters in elk van de contect secties. Standaard is 130 karakters. Een negatieve lengte geeft de complete body, terwijl een preview lengte van nul geen preview geeft.</p>\r\n<p><b>Resulaten naar voren halen?</b><br>\r\nAls de zoek resultaten naar voren gehaald moeten worden in de preview, vink dan dit vak aan</p>\r\n<p><b>Kleur highlights</b><br>\r\nDe kleuren die worden gebruikt om de corresponderende woorden naar voren te halen in de zoekopdracht.</p>', 1078414101);
